head.script:
var lastHash = 
var levelCookieName	= 'maxLogLevel';
var threadCookieName = 'selectedThreads';
var moduleFilterCookieName = 'selectedModules';
var currMaxLogLevel	 = 2;
var staticMaxLogLevel	= 4;
var logTables;
var threadOpt;
var threadIds = new Array();







/*  */
window.onload=function() {
	if(!location.hash)
		location.replace(lastHash);
	loadLogTables();
	initThreads();
	if(document.cookie) {
		var cookies = document.cookie.split(';');
		for( var iC=0; iC<cookies.length; ++iC ) {
			var nameVal = cookies[iC].split('=');
			if(nameVal.length!=2)
				continue;
			for( var iNv=0; iNv<nameVal.length; ++iNv ) {
				nameVal[iNv] = (nameVal[iNv].replace(/^\W+/,'')).replace(/\W+$/,'');
			}
			if(nameVal[0]==levelCookieName) {
				var ll = parseInt( nameVal[1] );
				if( ll != NaN && ll >= 0 && ll <= staticMaxLogLevel ) {
					currMaxLogLevel = ll;
				}
			}
			else if(nameVal[0]==threadCookieName) {
				var threadsSel = document.getElementById('SelectThreads');
				for( var iS=0; iS < threadsSel.length; ++iS )
					threadsSel[iS].selected = false;
				var tIds = nameVal[1].split('/');
				for( var iT=0; iT<tIds.length; ++iT )
					for( var iS=0; iS < threadsSel.length; ++iS )
						if( threadsSel[iS].text == tIds[iT] ) {
							threadsSel[iS].selected = true;
							break;
						}
			}
			else if(nameVal[0]==moduleFilterCookieName) {
				var moduleSel = document.getElementById('SelectModules');
				for( var iS=0; iS < moduleSel.length; ++iS )
					moduleSel[iS].selected = false;
				var mIds = nameVal[1].split('/');
				for( var iM=0; iM<mIds.length; ++iM )
					for( var iS=0; iS < moduleSel.length; ++iS )
						if( moduleSel[iS].text == mIds[iM] ) {
							moduleSel[iS].selected = true;
							break;
						}
			}
		}
	}
	updateModuleFilter();
	updateThreads();
	updateVisibility();
}
window.onunload=function() {
	var str = levelCookieName + '=' + String(currMaxLogLevel) + ';';
	document.cookie = str;
	str = threadCookieName + '=';
	var nPushed=0;
	var threadsSel = document.getElementById('SelectThreads');
	for( var sI=0; sI < threadsSel.length; sI++ )
		if( threadsSel[sI].selected == true ) {
			if(nPushed>0)
				str += '/';
			str += threadsSel[sI].text;
			nPushed++;
		}
	str += ';';
	document.cookie = str;
	str = moduleFilterCookieName + '=';
	var nPushed=0;
	var modulesSel = document.getElementById('SelectModules');
	for( var sI=0; sI < modulesSel.length; sI++ )
		if( modulesSel[sI].selected == true ) {
			if(nPushed>0)
				str += '/';
			str += modulesSel[sI].text;
			nPushed++;
		}
	str += ';';
	document.cookie = str;
	if( location.hash == lastHash )
		location.replace('');
}
function updateThreads() {
	threadOpt = new Array();
	var threadsSel = document.getElementById('SelectThreads');
	var someDeac=0;
	for( var idx=0; idx < threadsSel.length; idx++ )
		if( threadsSel[idx].selected == true )
			threadOpt[ Number(threadsSel[idx].text) ] = 1;
		else
			someDeac=1;
	var flyout = document.getElementById('ThreadFlyout');
	if(someDeac==1)
		flyout.style.color = 'red';
	else
		flyout.style.color = 'black';
}
function threadIsDeactivated( threadNb ) {
	return threadOpt[threadNb] != 1;
}
function updateModuleFilter() {
	var moduleSel = document.getElementById('SelectModules');
	var someDeac=0;
	for( var idx=0; idx < moduleSel.length; idx++ ) {
		if( moduleSel[idx].selected == true ) {
			var module_divs = document.getElementsByClassName('module_'+moduleSel[idx].value);
			for (var i = 0; i < module_divs.length; ++i) {
				module_divs[i].classList.remove('hidden');
			}
		}
		else {
			someDeac=1;
			var module_divs = document.getElementsByClassName('module_'+moduleSel[idx].value);
			for (var i = 0; i < module_divs.length; ++i) {
				module_divs[i].classList.add('hidden');
			}
		}
	}
	var flyout = document.getElementById('ModuleFilterFlyout');
	if(someDeac==1)
		flyout.style.color = 'red';
	else
		flyout.style.color = 'black';
}
function updateLocation() {
	if( location.hash )
		location.replace( location.hash );
}
function updateVisibility() {
	if(currMaxLogLevel > staticMaxLogLevel) {
		currMaxLogLevel = staticMaxLogLevel;
		return;
	}
	if(currMaxLogLevel < 0) {
			currMaxLogLevel = 0;
		return;
	}
	var txt;
	switch( currMaxLogLevel ) {
		case 0: txt = 'error'; break;
		case 1: txt = 'warning'; break;
		case 2: txt = 'info'; break;
		case 3: txt = 'verbose'; break;
		case 4: txt = 'debug'; break;
	}
	document.getElementById('maxLogLevelValue').firstChild.data = txt;
	for( var tabIdx = 0; tabIdx < logTables.length; tabIdx++ ) {
		var tab = logTables[tabIdx];
		for( var rowIdx = 1; rowIdx < tab.rows.length; rowIdx++ ) {
			var row = tab.rows[rowIdx];
			if( row.cells.length < 3 )
				continue;
			if( row.cells[2].firstChild.data > currMaxLogLevel || threadIsDeactivated( row.cells[3].firstChild.data ) )
				row.style.display = 'none';
			else
				row.style.display = '';
		}
	}
}
function logLevelIncrement() {
	currMaxLogLevel += 1;
	updateVisibility();
	updateLocation();
}
function logLevelDecrement() {
	currMaxLogLevel -= 1;
	updateVisibility();
	updateLocation();
}
function threadsAltered() {
	updateThreads();
	updateVisibility();
	updateLocation();
}
function modulesAltered() {
	updateModuleFilter();
	updateVisibility();
	updateLocation();
}
function initThreads() {
	if(threadIds.length == 0)
		return;
	for( var iThread = 0; iThread < threadIds.length; ++iThread ) {
		var opt = document.createElement('option');
		opt.text = threadIds[iThread];
		opt.selected=true;
		try {
			document.getElementById('SelectThreads').add(opt,null);
		}
		catch(ex) {
			document.getElementById('SelectThreads').add(opt);
		}
	}
}
function loadLogTables() {
	try {
		logTables = document.getElementsByClassName('Log');
	}
	catch(ex)
	{
		logTables = new Array();
		var tabs = document.getElementsByTagName('table');
		for( iTab in tabs ) {
			if( tabs[iTab].className == 'Log' )
				logTables.push( tabs[iTab] );
		}
	}
}
function showHideTableBody(tabBodyId,tabSwitchId) {
	var tabBody=document.getElementById(tabBodyId);
	if(tabBody.style.display=='none' || tabBody.style.display=='' ) {
		document.getElementById(tabSwitchId).innerHTML='-';
		tabBody.style.display='table-row-group';
	}
	else {
		document.getElementById(tabSwitchId).innerHTML='+';
		tabBody.style.display='none';
	}
}
function showHideLongValue(spanBodyId,switchId) {
	var spanElem=document.getElementById(spanBodyId);
	if(spanElem.style.maxHeight=='none' || spanElem.style.maxHeight=='' ) {
		document.getElementById(switchId).innerHTML='&#187;&#187;&#187;';
		spanElem.style.maxHeight='10em';
	}
	else {
		document.getElementById(switchId).innerHTML='&#171;&#171;&#171;';
		spanElem.style.maxHeight='';
	}
}
/*  */
